   * @param {string} firstName - User's first name
   * @param {string} token - Token generated during signup
   * @returns {boolean} specifies if the email was sent successfully
   */
  static verifyEmail(email, firstName, token) {
    const details = {
      email,
      subject: "Email Verification - Arca",
      html: `'<div style="width: 90%; margin: 5em auto;
       box-shadow: 0 0 10px rgba(0,0,0,.9);">
        <div>
          <div>
            <div style="background-color: #2084ba; height: 3rem; width: 100%">
                <h2 style="text-align: center; color: white;
                 padding-top: 10px;">Arca</h2>
            </div>
            <h4 style="text-align: center">Hi! ${firstName}</h4>
          </div>
          <div style=" padding: 0px 20px 20px 20px">
            <div>
              <p>Please verify that your email is <strong>${email}</strong>
               when you signed up.</p>
              <p>Click on the button below to verify.</p>
              <button style="color: white; background-color: #2084ba;
               border: none; border-radius: 10px; text-align: center;
                padding: 10px;">
                <a  href="${baseUrl}/auth/verify_email?token=${token}"
                 style="text-decoration: none;
                 color: white;">Verify Account</a></button>
            </div>
            <div>
              <h3 style="text-align: center">Thank you</h3>
              <h3 style="text-align: center">
              Please do not reply, this is an autogenerated email.</h3>
            </div>
          </div>
        </div>
      </div>`,
    };
    return SendEmail.emailSender(details);
  }

  /**
   * This function sends an email on verification of email address
   * @param {string} email - email address to send the message to
   * @param {string} token - Token generated during signup
   * @returns {boolean} specifies if a verification email was sent to user
   * after registration
   */
  static confirmRegistrationComplete(email) {
    const details = {
      email,
      subject: "Email Verification - Arca",
      html: `<p>Your registration has been completed<p>
      <p>Thank you for registering with Arca.</p>
       <p> >>>
       <a href=${baseUrl}/home> Go to your profile </a> <<< </p>`,
    };
    return SendEmail.emailSender(details);
  }

  /**
   * This function sends an email to reset password
   * @param {string} email - email address to send the message to
   * @returns {boolean} specifies if a verification email was sent to user
   * after registration
   */
  static resetPassword(email) {
    const details = {
      email,
      subject: "Reset Password - Soft Overflow",
      html: `<div>
        <p>Click on the button below to reset your password.</p>
        <button style="color: white; background-color: #2084ba; 
        border: none; border-radius: 10px; text-align: center;
        padding: 10px;">
        <a  href="${baseUrl}"
          style="text-decoration: none; color: white;">
        Reset Password</a></button>
        </div>`,
    };
    return SendEmail.emailSender(details);
  }

  /**
   *
   * @param {object} details - Object containing info for sending email
   * @returns {boolean} sends email to users
   */
  static async emailSender(details) {
    sendGrid.setApiKey(process.env.SENDGRID_API_KEY);
    const msg = {
      from: process.env.mail_master,
      html: details.html,
      subject: details.subject,
      to: details.email,
    };
    try {
      const isEmailSent = await sendGrid.send(msg);
      console.log("is email sent",isEmailSent)
      if (isEmailSent) {
        return true;
      }
    } catch (error) {
      return false;
    }
  }
}
export default SendEmail;

/**
 * Class representing the helper methods
 * @class HelperMethods
 * @description methods used everywhere in the codebase
 */
class HelperMethods {
  /**
   * A method used to send server errors
   * @param {object} res - HTTP response object
   * @param {String} message - The error message you want to set.
   * @returns {object} res - The HTTP response object
   */
  static serverError(res, message) {
    return res.status(500).json({
      success: false,
      message: message || 'Internal server error',
    });
  }

  /**
   * A method used to send client-side errors
   * @param {object} res - HTTP response object
   * @param {String} message - The error message you want to set.
   * @param {number} status = Status code of the client error
   * @returns {object} res - The HTTP response object
   */
  static clientError(res, message, status = 400) {
    return res.status(status).json({
      success: false,
      message,
    });
  }

  /**
   * A method used to confirm that a request was successful
   * @param {object} res - HTTP response object
   * @param {object} payload - data we want to send to the front-end
   * @param {number} status = Status code of the successful request
   * @returns {object} res - HTTP response object
   */
  static requestSuccessful(res, payload, status = 200) {
    return res.status(status).json({ success: true,...payload });
  }
}

export default HelperMethods;

import bcrypt from "bcrypt";

/**
 * @class CryptData
 * @description class defining the methods used to crypt and decrypt
 * sensitive information
 */
class CryptData {
  /**
   * @param {string} dataToEncrypt - string to encrypt
   * @return {string} - hashed string
   * @memberof CryptData
   */
  static async encryptData(dataToEncrypt) {
    const salt = 6;
    try {
      const hashedData = await bcrypt.hash(dataToEncrypt, salt);
      return hashedData;
    } catch (err) {
      return Error("An error occurred while hashing");
    }
  }

  /**
   * @param {string} dataToDecrypt - string to decrypt
   * @param {string} dataBaseHash - hash used to compare the string against
   * @return {boolean} - result of the comparison. If the passed in string
   * is the same as the hash used to compare, then a boolean 'true' is
   * returned else 'false' is returned.
   * @memberof CryptData
   */
  static async decryptData(dataToDecrypt, dataBaseHash) {
    try {
      const isPasswordCorrect = await bcrypt.compare(
        dataToDecrypt,
        dataBaseHash
      );
      return isPasswordCorrect;
    } catch (err) {
      return Error("An error occurred while comparing the data sent");
    }
  }
}
export default CryptData;

