import { Business } from "../models";
import CrudController from "./CrudController";
import { HelperMethods } from "../utils";

/**
 * Class representing the business controller
 * @class BusinessController
 * @description business controller
 */
class BusinessController {
  /**
   * Creates a new business
   * Route: POST: /business
   * @param {object} req - HTTP Request object
   * @param {object} res - HTTP Response object
   * @return {res} res - HTTP Response object
   * @memberof BusinessController
   */
  static async createBusiness(req, res) {
    const crudController = new CrudController(Business, "business", {
      businessName: req.body.businessName,
      customMessage:"Businesses must be unique"
    });
    return await crudController.create(req, res);
  }

  /**
   * Find all businesses
   * Route: POST: /businesses
   * @param {object} req - HTTP Request object
   * @param {object} res - HTTP Response object
   * @return {res} res - HTTP Response object
   * @memberof BusinessController
   */
  static async findAllBusinesses(req, res) {
    const crudController = new CrudController(Business, "business");
    return await crudController.findAll(req, res);
  }
  /**
   * Find one business
   * Route: GET: /business/:id
   * @param {object} req - HTTP Request object
   * @param {object} res - HTTP Response object
   * @return {res} res - HTTP Response object
   * @memberof BusinessController
   */
  static async findBusiness(req, res) {
    const crudController = new CrudController(Business, "business");
    return await crudController.findOne(req, res);
  }

  /**
   * Update a business
   * Route: PATCH: /business/:id
   * @param {object} req - HTTP Request object
   * @param {object} res - HTTP Response object
   * @return {res} res - HTTP Response object
   * @memberof BusinessController
   */
  static async updateBusiness(req, res) {
    const crudController = new CrudController(Business, "business");
    return await crudController.update(req, res);
  }

  /**
   * Delete a business
   * Route: DELETE: /business/:id
   * @param {object} req - HTTP Request object
   * @param {object} res - HTTP Response object
   * @return {res} res - HTTP Response object
   * @memberof BusinessController
   */
  static async deleteBusiness(req, res) {
    const crudController = new CrudController(Business, "business");
    return await crudController.deleteOne(req, res);
  }
}

export default BusinessController;

import { Business } from "../models";
import { HelperMethods } from "../utils";

/**
 * Class representing the business controller
 * @class CrudController
 * @description business controller
 */
class CrudController {
  constructor(model, modelName, unique = {}, criteria = {}) {
    this.Model = model;
    this.modelName = modelName;
    this.unique = unique;
    this.criteria = criteria;
  }
  /**
   * Creates a new data
   * @param {object} req - HTTP Request object
   * @param {object} res - HTTP Response object
   * @return {res} res - HTTP Response object
   * @memberof CrudController
   */
  async create(req, res) {
    const criteria = Object.keys(this.criteria).length
      ? this.criteria
      : { _id: req.params.id };
    try {
        const unique = {...this.unique}
        delete unique.customMessage
      if (Object.keys(this.unique).length) {
        const isExist =await this.Model.findOne(unique);
        if (isExist)
          return HelperMethods.clientError(
            res,
            this.unique.customMessage
          );
      }

      const newData = new this.Model(req.body);
      const savedData = await newData.save();
      if (savedData) {
        return HelperMethods.requestSuccessful(res, {
          success: true,
          [this.modelName]: savedData,
        });
      }
      return HelperMethods.clientError(
        res,
        `There was a problem creating this ${this.modelName}`
      );
    } catch (error) {
      return HelperMethods.serverError(res, error.message);
    }
  }

  /**
   * Find all data
   * @param {object} req - HTTP Request object
   * @param {object} res - HTTP Response object
   * @return {res} res - HTTP Response object
   * @memberof CrudController
   */
  async findAll(req, res) {
    try {
      const data = await this.Model.find(this.criteria);
      if (data && data.length) {
        return HelperMethods.requestSuccessful(res, {
          message: `${this.modelName} successfully listed`,
          [`${this.modelName}s`]: data,
        });
      }
      return HelperMethods.clientError(
        res,
        `No ${this.modelName}s was found`
      );
    } catch (error) {
      return HelperMethods.serverError(res, error.message);
    }
  }
  /**
   * Find one
   * @param {object} req - HTTP Request object
   * @param {object} res - HTTP Response object
   * @return {res} res - HTTP Response object
   * @memberof CrudController
   */
  async findOne(req, res) {
    const criteria = Object.keys(this.criteria).length
      ? this.criteria
      : { _id: req.params.id };
    try {
      const data = await this.Model.findOne(criteria)
      if (data) {
        return HelperMethods.requestSuccessful(res, {
          message: `${this.modelName} found successfully`,
          [this.modelName]:data,
        });
      }
      return HelperMethods.clientError(res, `${this.modelName} does not exist`);
    } catch (error) {
      return HelperMethods.serverError(res, error.message);
    }
  }

  /**
   * Update a data
   * @param {object} req - HTTP Request object
   * @param {object} res - HTTP Response object
   * @return {res} res - HTTP Response object
   * @memberof CrudController
   */
  async update(req, res) {
    const criteria = Object.keys(this.criteria).length
      ? this.criteria
      : { _id: req.params.id };
    try {
      const data = await this.Model.findOne(criteria);
      if (!data) {
        return HelperMethods.clientError(
          res,
          `${this.modelName} does not exist`
        );
      }
      const updatedData = await this.Model.updateOne(criteria, {
        $set: req.body,
      });
      if (updatedData) {
        return HelperMethods.requestSuccessful(res, {
          message: `${this.modelName} successfully updated`,
        });
      }
      return HelperMethods.clientError(
        res,
        `${this.modelName} could not be updated`
      );
    } catch (error) {
      return HelperMethods.serverError(res, error.message);
    }
  }

  /**
   * Delete data
   * @param {object} req - HTTP Request object
   * @param {object} res - HTTP Response object
   * @return {res} res - HTTP Response object
   * @memberof CrudController
   */
   async deleteOne(req, res) {
    const criteria = Object.keys(this.criteria).length
      ? this.criteria
      : { _id: req.params.id };
    try {
      const data = await this.Model.findOne(criteria);
      if (!data) {
        return HelperMethods.clientError(
          res,
          `${this.modelName} does not exist`
        );
      }
      const deletedData = await this.Model.deleteOne(criteria);
      if (deletedData) {
        return HelperMethods.requestSuccessful(res, {
          message: `${this.modelName} successfully deleted`,
        });
      }
      return HelperMethods.clientError(
        res,
        `${this.modelName} could not be deleted`
      );
    } catch (error) {
      return HelperMethods.serverError(res, error.message);
    }
  }
}

export default CrudController;

import mongoose from 'mongoose';
/**
 * Trims input values from user
 * @param {object} objectWithValuesToTrim - request body to trim
 * @returns {object} trimmedValues - trimmed values of the request object
 */
const trimValues = objectWithValuesToTrim => {
  const trimmedValues = objectWithValuesToTrim;
  Object.keys(trimmedValues).forEach(key => {
    trimmedValues[key] = trimmedValues[key].length
      ? trimmedValues[key].trim()
      : trimmedValues[key];
  });
  return trimmedValues;
};

/**
 * Defines the failed message returned when required fields are missing.
 * @param {object} res - Response object
 * @param {string} message - specific error message
 * @returns {res} - Response object
 */
const allFieldsRequired = (res, message) => {
  res.status(400).send({
    success: false,
    message: `Invalid request. '${message}' field is required`
  });
};

/**
 * Defines the failed message returned when required fields are missing.
 * @param {object} requestBody - HTTP request object
 * @returns {string} - Property of the request body object that is empty.
 */
const checkForEmptyFields = requestBody => {
  let result;
  Object.keys(requestBody).forEach(key => {
    if (!requestBody[key].length) result = key;
  });
  return result;
};

/**
 * class representing an handler's validation
 * @class Validate
 * @description Validation for user inputs in all requests
 */
class Validate {
  /**
   * @param {object} req - Request object
   * @param {object} res - Response object
   * @param {callback} next - The callback that passes the request to the next handler
   * @returns {object} res - Response object when query is invalid
   * @memberof Validate
   */
  static validateUserInput(req, res, next) {
    req.body = trimValues(req.body);
    const emptyField = checkForEmptyFields(req.body);
    if (emptyField) return allFieldsRequired(res, emptyField);
    next();
  }

  /**
  * @param {object} req - Request object
  * @param {object} res - Response object
  * @param {callback} next - The callback that passes the request to the next handler
  * @returns {object} res - Response object when query is invalid
  * @memberof Validate
  */
  static validateUserEmail(req, res, next) {
    req.body = trimValues(req.body);
    const emptyField = checkForEmptyFields(req.body);
    if (emptyField) return allFieldsRequired(res, emptyField);
    if (!req.body.email) return allFieldsRequired(res, 'email');
    next();
  }

  /**
  * @param {object} req - Request object
  * @param {object} res - Response object
  * @param {callback} next - The callback that passes the request to the next handler
  * @returns {object} res - Response object when query is invalid
  * @memberof Validate
  */
  static validateID(req, res, next) {
    if (!mongoose.Types.ObjectId.isValid(req.params.id)) {
      return allFieldsRequired(res, 'A valid ID');
    }
    next();
  }

  /**
   * @param {object} req - Request object
   * @param {object} res - Response object
   * @param {callback} next - The callback that passes the request to the next handler
   * @returns {object} res - Response object when query is invalid
   * @memberof Validate
   */
  static validateUserLogin(req, res, next) {
    req.body = trimValues(req.body);
    const { email, password } = req.body;
    if (!email) return allFieldsRequired(res, 'email');
    if (!password) return allFieldsRequired(res, 'password');
    next();
  }
}
export default Validate;

